function obtn(){
  let localelem = localStorage.getItem("Kino");
  let parsed = JSON.parse(localelem);
  console.table("Elements",parsed);
  let elem = document.createElement('div');
  elem.id="elem";
  result.appendChild(elem);
  elem.style.color = 'white';
  for(let i=0; i<=71;i++){
  elem.innerHTML += `<header><div>Film №${i+1}</div>
  <p>Id:  ${JSON.stringify(parsed.docs[i].id)}</p>
  <p>Author:  ${JSON.stringify(parsed.docs[i].author)}</p>
  <p>movieID:  ${JSON.stringify(parsed.docs[i].movieId)}</p>
  <p>Title:  ${JSON.stringify(parsed.docs[i].title)}</p>
  <p>Review:  ${JSON.stringify(parsed.docs[i].review)}</p>
  </header><br>`;
  console.log(parsed.docs[i]);
  }
}





